let restaurants,neighborhoods,cuisines;var newMap;var markers=[];document.addEventListener('DOMContentLoaded',(event)=>{initMap();fetchNeighborhoods();fetchCuisines()});fetchNeighborhoods=()=>{DBHelper.fetchNeighborhoods((error,neighborhoods)=>{if(error){console.error(error)}else{self.neighborhoods=neighborhoods;fillNeighborhoodsHTML()}})}
fillNeighborhoodsHTML=(neighborhoods=self.neighborhoods)=>{const select=document.getElementById('neighborhoods-select');neighborhoods.forEach(neighborhood=>{const option=document.createElement('option');option.innerHTML=neighborhood;option.value=neighborhood;select.append(option)})}
fetchCuisines=()=>{DBHelper.fetchCuisines((error,cuisines)=>{if(error){console.error(error)}else{self.cuisines=cuisines;fillCuisinesHTML()}})}
fillCuisinesHTML=(cuisines=self.cuisines)=>{const select=document.getElementById('cuisines-select');cuisines.forEach(cuisine=>{const option=document.createElement('option');option.innerHTML=cuisine;option.value=cuisine;select.append(option)})}
initMap=()=>{self.newMap=L.map('map',{center:[40.722216,-73.987501],zoom:12,scrollWheelZoom:!1});L.tileLayer('https://api.tiles.mapbox.com/v4/{id}/{z}/{x}/{y}.jpg70?access_token={mapboxToken}',{mapboxToken:'pk.eyJ1IjoianVsYmFycyIsImEiOiJjamo2b2F1cDQwZ3R6M3F0ZWI5Mml5ZmVrIn0.GYzP56ZNJiXR8w_qNNO5BA',maxZoom:18,attribution:'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors, '+'<a href="https://creativecommons.org/licenses/by-sa/2.0/">CC-BY-SA</a>, '+'Imagery Â© <a href="https://www.mapbox.com/">Mapbox</a>',id:'mapbox.streets'}).addTo(newMap);updateRestaurants()}
updateRestaurants=()=>{const cSelect=document.getElementById('cuisines-select');const nSelect=document.getElementById('neighborhoods-select');const cIndex=cSelect.selectedIndex;const nIndex=nSelect.selectedIndex;const cuisine=cSelect[cIndex].value;const neighborhood=nSelect[nIndex].value;DBHelper.fetchRestaurantByCuisineAndNeighborhood(cuisine,neighborhood,(error,restaurants)=>{if(error){console.error(error)}else{resetRestaurants(restaurants);fillRestaurantsHTML()}})}
resetRestaurants=(restaurants)=>{self.restaurants=[];const ul=document.getElementById('restaurants-list');ul.innerHTML='';if(self.markers){self.markers.forEach(marker=>marker.remove())}
    self.markers=[];self.restaurants=restaurants}
fillRestaurantsHTML=(restaurants=self.restaurants)=>{const ul=document.getElementById('restaurants-list');restaurants.forEach(restaurant=>{ul.append(createRestaurantHTML(restaurant))});addMarkersToMap()}
createRestaurantHTML=(restaurant)=>{const li=document.createElement('li');const image=document.createElement('img');image.className='restaurant-img';image.src=DBHelper.thumbUrlForRestaurant(restaurant)+'.webp';image.alt=restaurant.name;if(!image.src.includes('undefined')){li.append(image)}
    const name=document.createElement('h1');name.innerHTML=restaurant.name;li.append(name);const isFavorite=document.createElement('span');li.append(isFavorite);const isFavoriteCheck=document.createElement('input');isFavoriteCheck.type='checkbox';isFavoriteCheck.setAttribute('id',`favoriteRestaurant${restaurant.id}`);(restaurant.is_favorite===!0)?isFavoriteCheck.checked=!0:isFavoriteCheck.checked=!1;isFavorite.append(isFavoriteCheck);isFavoriteCheck.addEventListener('change',()=>{DBHelper.fetchFavoriteRestaurant(restaurant.id,isFavoriteCheck.checked)});const isFavoriteLabel=document.createElement('label');isFavoriteLabel.setAttribute('for',`favoriteRestaurant${restaurant.id}`);isFavoriteLabel.innerText='Favourite restaurant';isFavorite.append(isFavoriteLabel);const neighborhood=document.createElement('p');neighborhood.innerHTML=restaurant.neighborhood;li.append(neighborhood);const address=document.createElement('p');address.innerHTML=restaurant.address;li.append(address);const more=document.createElement('a');more.innerHTML='View Details';more.href=DBHelper.urlForRestaurant(restaurant);li.append(more)
    return li}
addMarkersToMap=(restaurants=self.restaurants)=>{restaurants.forEach(restaurant=>{const marker=DBHelper.mapMarkerForRestaurant(restaurant,self.newMap);marker.on("click",onClick);function onClick(){window.location.href=marker.options.url}
    self.markers.push(marker)})}